<script setup>
  const { gsap, ScrollTrigger } = useGsap()
  function heartBeat() {
    gsap
      .to('#corazon', {
        duration: 1.5,
        drawSVG: '20% 20%',
        transformOrigin: 'center center',
        yoyo: true,
        repeat: -1,
      })
      .timeScale(0.5)
  }

  function wordTl() {
    let tl = gsap
      .timeline({ paused: true })
      .to('#word', {
        y: '+=20',
        fillOpacity: 0.6,
        strokeOpacity: 0,
      })
      .to(
        '#trazado',
        {
          y: '+=30',
          autoAlpha: 0,
        },
        '-=0.5'
      )
      .to(
        '#corazon',
        {
          y: '+=30',
          scale: 2,
          autoAlpha: 0,
        },
        '-=0.5'
      )

    return tl
  }

  function smTl() {
    ScrollTrigger.create({
      id: 'stMenu',
      trigger: '.content',
      start: 'top top',
      end: 'bottom top',
      pin: true,
      pinSpacing: false,
      scrub: 1,
      snap: 1,
      animation: wordTl(),
    })
  }

  function tlMaster() {
    gsap.timeline().add(heartBeat()).add(smTl())
  }

  defineExpose({
    tlMaster,
  })
</script>
